pragma solidity ^0.4.24;
// import "./RedBlackTree.sol";
import {Bits} from "./imported/Bits.sol";
import "./imported/strings.sol";
import "./imported/verify.sol";
pragma experimental "ABIEncoderV2";

contract Owner {
    verify.Data data;
    using strings for *;
    // using verify for *;
    using verify for verify.Data;

    address public owner;
    mapping (address => uint) public balances;

    using Bits for uint8;
    string name = "Owner";
    mapping (string => uint8) bit_map;
    uint8 recursive_map = 0;
    uint8 ticket_counter;
    uint8 fixed_size = 8;
    uint8 index = 0; 
    uint8 end = 7;
    uint8 start = 0;

    uint public a = 1;
    uint public nounce = 1;
    uint public time_period = 60;

    bytes32 verify_private_key;
    address verify_public_key;
    uint access_time;

    function Owner(address _t,  bytes32 private_key) public {
        owner = msg.sender;
        balances[msg.sender] = 10001;
        verify_private_key = generatePrivateKey(private_key);
    }

    function getVerifyPrivateKeyAndPolicy() public returns (bytes32, address, uint, uint, string) {
        string memory name = "Owner";
        return (verify_private_key, owner, balances[owner], time_period, name);
    }

    function generatePrivateKey(bytes32 private_key) private returns (bytes32){
        bytes32 pk = keccak256(private_key, nounce);
        nounce = nounce + 1;
        return pk;
    }

    function setVerifyPublicKey(address public_key) public {
        verify_public_key = public_key;
    }

    function moveBuffer(uint8 counter) private returns(uint8){
        uint8 minimal_size = counter - end;
        uint8 old_index = index;
        uint8 move_time = 0;

        // move_time = 0;
        for (uint8 i=0; i<fixed_size; i++){
            if (bit_map[name].bit(index) == 0 && (move_time + 1) >= minimal_size){
                index = (index + 1) % fixed_size;
                end = end + move_time + 1;
                start = end - fixed_size + 1;
                // move = move_time;
                return (minimal_size + old_index - 1) % fixed_size;
            }else{
                uint8 new_bit = bit_map[name].clearBit(index);
                bit_map[name] = new_bit;
                index = (index + 1) % fixed_size;
                move_time += 1;
            }
        }
    }

    function getA() public returns (uint, uint8, address) {
        return (a, bit_map["Owner"], verify_public_key);
    }

    function setA(uint _a, bytes32[] _addr, uint[] _type, uint[] _time, uint8[] _index, byte[] _v, bytes32[] _r, bytes32[] _s) public {
        uint belong_index = 0;
        if (_addr.length == 1){
            if (_type[0] == 4){
                // assert(verify_public_key == data.verifyTickets4(_addr[0], _type[0], _time[0], _index[0],  _v[0],  _r[0], _s[0], msg.sender, msg.sig, msg.gas));
                a = _a;
            }else if (_type[0] == 5){
                // assert(verify_public_key == data.verifyTickets4(_addr[0], _type[0], _time[0], _index[0],  _v[0],  _r[0], _s[0], msg.sender, msg.sig, msg.gas));
                a = _a;
            }else {
                // assert(verify_public_key == data.verifyTickets(_addr[0], _type[0], _time[0], _index[0],  _v[0],  _r[0], _s[0], msg.sender, msg.sig, msg.data));
                a = _a;
            }
        }else{
            while (belong_index < _addr.length ){
                if (bytesToBytes32(toBytes(address(this))) == _addr[belong_index]){
                    break;
                }
                belong_index += 1;
            }
            assert(verify_public_key == data.verifyTickets(_addr[belong_index], _type[belong_index], _time[belong_index], _index[belong_index],  _v[belong_index],  _r[belong_index], _s[belong_index], msg.sender, msg.sig, msg.data));
            a = _a;
        }
    }

    function bytesToBytes32(bytes memory source) private pure returns (bytes32 result) {
        assembly { 
            //mload(p) -->  mem[p..(p+32)) 
            result := mload(add(source, 32))
        }
    }

    function toBytes(address x) private returns (bytes b) {
        b = new bytes(20);
        for (uint i = 0; i < 20; i++)
            b[i] = byte(uint8(uint(x) / (2**(8*(19 - i)))));
    }
}
